package com.xworkz.control;

public class FounderName {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String Company="Google";
		if(Company=="Google") {
			System.out.println("The company "+ Company+ " was founded by Larry Page ");
		}
		else if(Company=="Facebook") {
			System.out.println("The company "+ Company+ " was founded by Mark Zuckerberg ");
		}
		else if(Company=="Infosys"){
			System.out.println("The company "+ Company+ " was founded by N.r. Narayana Murthy  ");
		}
		else if(Company=="IBM"){
			System.out.println("The company "+ Company+ " was founded by Charles Ranlett Flint ");
		}
		
	}

}
